(function() {
  module.exports = {
    decode: function(obj) {
      return new Date(obj.toString('utf8'));
    },
    encode: function(date) {
      return new Buffer(date.toJSON());
    },
    register: function(msgpack) {
      msgpack.register(0x42, Date, this.encode, this.decode);
      return msgpack;
    }
  };

}).call(this);
