(function() {
  "use strict";
  var getNewTmpDir, getNewTmpFile, mktemp;

  mktemp = require('mktemp');

  getNewTmpDir = function() {
    return mktemp.createDirSync('/tmp/dir-XXX');
  };

  getNewTmpFile = function(extension) {
    var filePathPattern;
    filePathPattern = '/tmp/file-XXX';
    if (extension != null) {
      filePathPattern += "." + extension;
    }
    return mktemp.createFileSync(filePathPattern);
  };

  module.exports = {
    getNewTmpDir: getNewTmpDir,
    getNewTmpFile: getNewTmpFile
  };

}).call(this);
